<?php class db_connection {public $db_name;public $db_host;public $db_user;public $db_password;public $db_charset;public function __construct($db_name, $db_host, $db_user, $db_password, $db_charset = 'UTF-8'){$this->db_name = $db_name;$this->db_host = $db_host;$this->db_user = $db_user;$this->db_password = $db_password;$this->db_charset = $db_charset;}public function createConnection(){$_host = $this->db_host;$_user = $this->db_user;$_password = $this->db_password;$_name = $this->db_name;$dbc = @mysqli_connect($_host, $_user, $_password, $_name) or die("There was an error causing the dataBase Connection to fail, please check that you have inserted all the necessary, corresponding data required:<br/><br/>" . mysqli_connect_error() );mysqli_set_charset($dbc, $this->db_charset);}public function editHost($newHost){if(!isset($_host)){echo "Please Define a Connection before changing the host.";}else{$_host = $newHost;$this->createConnection();}}public function edituser($newUser){if(!isset($_user)){echo "Please Define a Connection before changing the User.";}else{$_user = $newUser;$this->createConnection();}}public function editpassword($newPassword){if(!isset($_password)){echo "Please Define a Connection before changing the Password.";}else{$_password = $newPassword;$this->createConnection();}}public function editname($newName){if(!isset($_name)){echo "Please Define a Connection before changing the Name.";}else{$_name = $newName;$this->createConnection();}}} ?>
